import React, { useState, useMemo } from 'react';
import { useDrag, useDrop } from 'react-dnd';

// Types de drag & drop
const ItemTypes = {
    TASK: 'task'
};

// Composant Task draggable optimis√©
const TaskItem = ({ task, category, index, onTaskClick }) => {
    const [{ isDragging }, dragRef] = useDrag(() => ({
        type: ItemTypes.TASK,
        item: {
            type: ItemTypes.TASK,
            task,
            category,
            index,
            sourceCategory: category,
            sourceIndex: index
        },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    }), [task, category, index]);

    return (
        <div
            ref={dragRef}
            className={`task-item clickable draggable enhanced-drag compact ${isDragging ? 'dragging' : ''}`}
            onClick={() => !isDragging && onTaskClick(task)}
            title="üñ±Ô∏è Glissez pour r√©organiser dans la m√™me cat√©gorie"
            style={{ opacity: isDragging ? 0.6 : 1 }}
        >
            <div className="task-content-compact">
                <span className="drag-handle enhanced-handle compact-handle" title="R√©organiser">
                    ‚ãÆ‚ãÆ
                </span>
                <div className="task-info">
                    <span className="task-number">
                        {task.fields?.find(f => f.key === 'modelNumber')?.value}
                    </span>
                    <span className="task-name-compact">
                        {task.name}
                    </span>
                </div>
            </div>
        </div>
    );
};

// Zone de drop optimis√©e
const TaskDropZone = ({ category, index, onReorderTask, isLast = false }) => {
    const [{ isOver, canDrop }, dropRef] = useDrop(() => ({
        accept: ItemTypes.TASK,
        drop: (item) => {
            if (item.sourceCategory === category) {
                let targetIndex = index;
                if (item.sourceIndex < index) {
                    targetIndex = index - 1;
                }
                if (item.sourceIndex !== targetIndex && targetIndex >= 0) {
                    onReorderTask(category, item.sourceIndex, targetIndex);
                }
            }
            return { dropEffect: 'move' };
        },
        canDrop: (item) => item.sourceCategory === category,
        collect: (monitor) => ({
            isOver: monitor.isOver(),
            canDrop: monitor.canDrop(),
        }),
    }), [category, index, onReorderTask]);

    const showDropIndicator = isOver && canDrop;
    const isDragOverDifferentCategory = isOver && !canDrop;

    return (
        <div
            ref={dropRef}
            className={`task-drop-zone-vertical ${showDropIndicator ? 'drag-over' : ''} ${isDragOverDifferentCategory ? 'blocked-drop' : ''} ${isLast ? 'final' : ''}`}
        >
            {showDropIndicator && !isLast && (
                <div className="drop-indicator">‚ñº Ins√©rer ici ‚ñº</div>
            )}
            {showDropIndicator && isLast && (
                <div className="drop-indicator final">‚¨áÔ∏è Ajouter √† la fin</div>
            )}
            {isDragOverDifferentCategory && (
                <div className="drop-indicator blocked">‚ùå Drop inter-cat√©gories bloqu√©</div>
            )}
        </div>
    );
};

// Zone de drop pour cat√©gories vides
const EmptyDropZone = ({ category }) => {
    const [{ isOver, canDrop }, dropRef] = useDrop(() => ({
        accept: ItemTypes.TASK,
        canDrop: (item) => item.sourceCategory === category,
        collect: (monitor) => ({
            isOver: monitor.isOver(),
            canDrop: monitor.canDrop(),
        }),
    }), [category]);

    const showDropIndicator = isOver && canDrop;
    const isDragOverDifferentCategory = isOver && !canDrop;

    return (
        <div
            ref={dropRef}
            className={`empty-category-drop-zone grid-full-width ${showDropIndicator ? 'drag-over' : ''} ${isDragOverDifferentCategory ? 'blocked-drop' : ''}`}
        >
            üìÇ Aucune t√¢che dans "{category}"
            {showDropIndicator && (
                <div className="empty-drop-indicator">‚ú® Zone de r√©organisation</div>
            )}
            {isDragOverDifferentCategory && (
                <div className="empty-drop-indicator blocked">‚ùå Drop inter-cat√©gories non autoris√©</div>
            )}
        </div>
    );
};

// Composant de colonne de cat√©gorie
const CategoryColumn = ({ category, tasks, isOpen, isAdminMode, onToggleCategory, onTaskClick, onReorderTask, searchTerm }) => {
    // Filtrer les t√¢ches localement
    const filteredTasks = useMemo(() => {
        if (!searchTerm) return tasks;

        const searchLower = searchTerm.toLowerCase();
        return tasks.filter(task => {
            const modelNumber = task.fields?.find(f => f.key === 'modelNumber')?.value || '';
            return (
                task.name.toLowerCase().includes(searchLower) ||
                modelNumber.toLowerCase().includes(searchLower) ||
                category.toLowerCase().includes(searchLower)
            );
        });
    }, [tasks, searchTerm, category]);

    return (
        <div className="category-section">
            <div
                className={`category-header ${isAdminMode ? 'admin-mode' : ''}`}
                onClick={(e) => onToggleCategory(category, e)}
            >
                <div className="category-left">
                    <span className="category-toggle">
                        {isOpen ? '‚ñº' : '‚ñ∂'}
                    </span>
                    <span className="category-name">{category}</span>
                    {isAdminMode && (
                        <span className="task-count">({filteredTasks.length})</span>
                    )}
                </div>
                <span className="category-goto">‚Üí</span>
            </div>

            {isOpen && (
                <div className="tasks-grid">
                    {filteredTasks.length === 0 ? (
                        <EmptyDropZone category={category} />
                    ) : (
                        <>
                            {filteredTasks.map((task, index) => (
                                <div key={`task-wrapper-${task.tabName}-${task.id}`} className="task-grid-item">
                                    <TaskDropZone
                                        category={category}
                                        index={index}
                                        onReorderTask={onReorderTask}
                                    />
                                    <TaskItem
                                        task={task}
                                        category={category}
                                        index={index}
                                        onTaskClick={onTaskClick}
                                    />
                                </div>
                            ))}
                            <TaskDropZone
                                category={category}
                                index={filteredTasks.length}
                                onReorderTask={onReorderTask}
                                isLast={true}
                            />
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

const DashboardMaison = ({ 
    appData, 
    onTabChange, 
    onTaskSelect, 
    onAdminPanel, 
    isAdminMode, 
    onMoveTask, 
    onReorderCategories, 
    onReorderTasksInCategory 
}) => {
    const [categoryStates, setCategoryStates] = useState({});
    const [searchTerm, setSearchTerm] = useState('');

    // Donn√©es organis√©es par cat√©gorie - calcul optimis√©
    const categoriesData = useMemo(() => {
        const categories = new Map();

        Object.keys(appData).forEach(tabName => {
            if (tabName !== 'Maison' && tabName !== 'Admin') {
                const tasks = appData[tabName] || [];
                tasks.forEach(task => {
                    const categoryName = task.category || 'Autres';

                    if (!categories.has(categoryName)) {
                        categories.set(categoryName, {
                            name: categoryName,
                            tasks: []
                        });
                    }

                    categories.get(categoryName).tasks.push({
                        ...task,
                        tabName
                    });
                });
            }
        });

        // Retourner un array tri√© pour un ordre stable
        return Array.from(categories.values())
            .sort((a, b) => a.name.localeCompare(b.name));
    }, [appData]);

    // Filtrage des cat√©gories
    const filteredCategories = useMemo(() => {
        if (!searchTerm) return categoriesData;

        const searchLower = searchTerm.toLowerCase();

        return categoriesData.filter(category => {
            const categoryMatches = category.name.toLowerCase().includes(searchLower);
            const hasMatchingTasks = category.tasks.some(task => {
                const modelNumber = task.fields?.find(f => f.key === 'modelNumber')?.value || '';
                return (
                    task.name.toLowerCase().includes(searchLower) ||
                    modelNumber.toLowerCase().includes(searchLower)
                );
            });

            return categoryMatches || hasMatchingTasks;
        });
    }, [categoriesData, searchTerm]);

    const handleToggleCategory = (categoryName, event) => {
        event.stopPropagation();
        setCategoryStates(prev => ({
            ...prev,
            [categoryName]: !prev[categoryName]
        }));
    };

    const handleTaskClick = (task) => {
        onTabChange(task.tabName);
        onTaskSelect(task);
    };

    const getTotalTaskCount = () => {
        return filteredCategories.reduce((total, category) => total + category.tasks.length, 0);
    };

    return (
        <div className="dashboard-container">
            <div className="dashboard-header">
                <h1 className="dashboard-title">Covalen</h1>
            </div>

            <p className="welcome-message">
                Bienvenue sur votre tableau de bord centralis√©.
            </p>

            {isAdminMode && (
                <div className="admin-dashboard-section">
                    <div className="admin-banner">
                        <p className="admin-banner-text">Mode Admin Actif sur le Dashboard.</p>
                        <button className="admin-manage-btn" onClick={onAdminPanel}>
                            G√©rer les Onglets
                        </button>
                    </div>
                </div>
            )}

            <div className="overview-section">
                <h2 className="overview-title">Vue d'overview des T√¢ches</h2>

                <div className="search-container">
                    <div className="search-bar">
                        <span className="search-icon">üîç</span>
                        <input
                            type="text"
                            placeholder="Rechercher une t√¢che, cat√©gorie ou num√©ro..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="search-input"
                        />
                        {searchTerm && (
                            <button
                                className="search-clear"
                                onClick={() => setSearchTerm('')}
                                title="Effacer la recherche"
                            >
                                ‚úï
                            </button>
                        )}
                    </div>
                    <div className="search-hints">
                        <span className="search-hint">üí° Raccourci: Ctrl+/ pour rechercher</span>
                    </div>
                    {searchTerm && (
                        <div className="search-results-info">
                            {filteredCategories.length === 0
                                ? `Aucun r√©sultat pour "${searchTerm}"`
                                : `${getTotalTaskCount()} t√¢che(s) trouv√©e(s) dans ${filteredCategories.length} cat√©gorie(s)`
                            }
                        </div>
                    )}
                </div>

                <div className="categories-container">
                    <div className="tasks-header">
                        <span className="tasks-label">T√¢ches</span>
                    </div>

                    {filteredCategories.map(category => (
                        <CategoryColumn
                            key={category.name}
                            category={category.name}
                            tasks={category.tasks}
                            isOpen={categoryStates[category.name] ?? true}
                            isAdminMode={isAdminMode}
                            onToggleCategory={handleToggleCategory}
                            onTaskClick={handleTaskClick}
                            onReorderTask={onReorderTasksInCategory}
                            searchTerm={searchTerm}
                        />
                    ))}

                    {filteredCategories.length === 0 && (
                        <div className="no-results">
                            <p>Aucune cat√©gorie ne correspond √† votre recherche.</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default DashboardMaison;
