import React, { useState, useMemo } from 'react';

const DashboardMaison = ({ appData, onTabChange, onTaskSelect, onAdminPanel, isAdminMode, onMoveTask, onReorderCategories, onReorderTasksInCategory }) => {
    const [categoryStates, setCategoryStates] = useState({});
    const [searchTerm, setSearchTerm] = useState('');
    const [draggedItem, setDraggedItem] = useState(null);
    const [dragOverCategory, setDragOverCategory] = useState(null);

    // --- Fonctions Drag & Drop Simplifi√©es ---
    const handleDragStart = (e, task, category, index) => {
        console.log('üöÄ Drag started:', { task: task.name, category, index });

        // Cr√©er les donn√©es de drag
        const dragData = {
            taskId: task.id,
            taskName: task.name,
            sourceCategory: category,
            sourceIndex: index,
            tabName: task.tabName,
            fullTask: task
        };

        // Stocker dans dataTransfer ET √©tat local
        e.dataTransfer.setData('application/json', JSON.stringify(dragData));
        e.dataTransfer.effectAllowed = 'move';

        // Style visuel pour le drag
        e.target.style.opacity = '0.6';
        e.target.style.transform = 'scale(1.02)';

        setDraggedItem(dragData);
    };

    const handleDragEnd = (e) => {
        console.log('üèÅ Drag ended');
        e.target.style.opacity = '1';
        e.target.style.transform = 'scale(1)';
        setDraggedItem(null);
        setDragOverCategory(null);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
        e.dataTransfer.dropEffect = 'move';
    };

    const handleDragEnter = (e, category) => {
        e.preventDefault();
        console.log('üì• Drag enter:', category);
        setDragOverCategory(category);
    };

    const handleDragLeave = (e) => {
        if (!e.currentTarget.contains(e.relatedTarget)) {
            setDragOverCategory(null);
        }
    };

    const handleDrop = (e, targetCategory, targetIndex = null) => {
        e.preventDefault();
        console.log('üéØ Drop on category:', targetCategory, 'at index:', targetIndex);

        // R√©cup√©rer les donn√©es
        let dragData = draggedItem;
        if (!dragData) {
            try {
                const jsonData = e.dataTransfer.getData('application/json');
                if (jsonData) {
                    dragData = JSON.parse(jsonData);
                    console.log('üì¶ Retrieved from dataTransfer:', dragData);
                }
            } catch (error) {
                console.log('‚ùå Error parsing drag data:', error);
            }
        }

        if (!dragData) {
            console.log('‚ùå No drag data available');
            setDragOverCategory(null);
            return;
        }

        const { sourceCategory, sourceIndex, fullTask } = dragData;

        if (sourceCategory !== targetCategory) {
            // D√©placer vers une nouvelle cat√©gorie
            console.log('üîÑ Moving task:', sourceCategory, '‚Üí', targetCategory);
            if (onMoveTask && fullTask) {
                onMoveTask(fullTask, targetCategory);
                console.log('‚úÖ Task moved successfully');
            }
        } else if (targetIndex !== null && sourceIndex !== targetIndex) {
            // R√©organiser dans la m√™me cat√©gorie
            console.log('ÔøΩ Reordering task in same category:', sourceIndex, '‚Üí', targetIndex);
            if (onReorderTasksInCategory) {
                onReorderTasksInCategory(sourceCategory, sourceIndex, targetIndex);
                console.log('‚úÖ Task reordered successfully');
            }
        } else {
            console.log('üîÑ Same position, no action needed');
        }

        setDragOverCategory(null);
        setDraggedItem(null);
    };
    const getAllTasksByCategory = () => {
        const allCategories = {};

        // Parcourir tous les onglets sauf 'Maison' et 'Admin'
        Object.keys(appData).forEach(tabName => {
            if (tabName !== 'Maison' && tabName !== 'Admin') {
                const tasks = appData[tabName] || [];
                tasks.forEach(task => {
                    const category = task.category || 'Autres';
                    if (!allCategories[category]) {
                        allCategories[category] = [];
                    }
                    allCategories[category].push({
                        ...task,
                        tabName
                    });
                });
            }
        });

        return allCategories;
    };

    const allTasksByCategory = getAllTasksByCategory();

    // Filtrer les t√¢ches selon le terme de recherche
    const filteredCategories = useMemo(() => {
        if (!searchTerm) return allTasksByCategory;

        const filtered = {};
        const searchLower = searchTerm.toLowerCase();

        const taskMatchesSearch = (task) => {
            const modelNumber = task.fields?.find(f => f.key === 'modelNumber')?.value || '';
            return (
                task.name.toLowerCase().includes(searchLower) ||
                modelNumber.toLowerCase().includes(searchLower)
            );
        };

        Object.keys(allTasksByCategory).forEach(category => {
            const matchingTasks = allTasksByCategory[category].filter(taskMatchesSearch);
            const categoryMatches = category.toLowerCase().includes(searchLower);

            if (matchingTasks.length > 0 || categoryMatches) {
                filtered[category] = matchingTasks;
            }
        });

        return filtered;
    }, [allTasksByCategory, searchTerm]);

    const categorizedTasks = filteredCategories;

    const handleToggleCategory = (category, event) => {
        event.stopPropagation();
        setCategoryStates(prev => ({
            ...prev,
            [category]: !prev[category]
        }));
    };

    const handleTaskClick = (task) => {
        // Naviguer vers l'onglet et s√©lectionner la t√¢che
        onTabChange(task.tabName);
        onTaskSelect(task);
    };

    const getCategoryIcon = (isOpen) => {
        return isOpen ? '‚ñº' : '‚ñ∂';
    };

    const getGoToIcon = () => {
        return '‚Üí';
    };

    return (
        <div className="dashboard-container">
            {/* En-t√™te principal */}
            <div className="dashboard-header">
                <h1 className="dashboard-title">Covalen</h1>
            </div>

            {/* Message de bienvenue */}
            <p className="welcome-message">
                Bienvenue sur votre tableau de bord centralis√©.
            </p>

            {/* Section Admin - seulement visible en mode admin */}
            {isAdminMode && (
                <div className="admin-dashboard-section">
                    <div className="admin-banner">
                        <p className="admin-banner-text">Mode Admin Actif sur le Dashboard.</p>
                        <button className="admin-manage-btn" onClick={onAdminPanel}>
                            G√©rer les Onglets
                        </button>
                    </div>
                </div>
            )}            {/* Section Vue d'overview */}
            <div className="overview-section">
                <h2 className="overview-title">Vue d'overview des T√¢ches</h2>

                {/* Barre de recherche */}
                <div className="search-container">
                    <div className="search-bar">
                        <span className="search-icon">üîç</span>
                        <input
                            type="text"
                            placeholder="Rechercher une t√¢che, cat√©gorie ou num√©ro..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="search-input"
                        />
                        {searchTerm && (
                            <button
                                className="search-clear"
                                onClick={() => setSearchTerm('')}
                                title="Effacer la recherche"
                            >
                                ‚úï
                            </button>
                        )}
                    </div>
                    <div className="search-hints">
                        <span className="search-hint">üí° Raccourci: Ctrl+/ pour rechercher</span>
                    </div>
                    {searchTerm && (
                        <div className="search-results-info">
                            {Object.keys(categorizedTasks).length === 0
                                ? `Aucun r√©sultat pour "${searchTerm}"`
                                : `${Object.values(categorizedTasks).flat().length} r√©sultat(s) trouv√©(s)`
                            }
                        </div>
                    )}
                </div>

                {/* Conteneur des cat√©gories */}
                <div className="categories-container">
                    <div className="tasks-header">
                        <span className="tasks-label">T√¢ches</span>
                    </div>

                    {/* Liste des cat√©gories */}
                    {Object.keys(categorizedTasks).map(category => {
                        const isOpen = categoryStates[category] ?? true;
                        const tasks = categorizedTasks[category];

                        return (
                            <div
                                key={category}
                                className={`category-section ${dragOverCategory === category ? 'drag-over' : ''}`}
                                onDragOver={handleDragOver}
                                onDragEnter={(e) => handleDragEnter(e, category)}
                                onDragLeave={handleDragLeave}
                                onDrop={(e) => handleDrop(e, category)}
                            >
                                {/* En-t√™te de cat√©gorie */}
                                <div
                                    className={`category-header ${isAdminMode ? 'admin-mode' : ''}`}
                                    onClick={(e) => handleToggleCategory(category, e)}
                                >
                                    <div className="category-left">
                                        {isAdminMode && <span className="drag-handle">‚ãÆ‚ãÆ</span>}
                                        <span className="category-toggle">
                                            {getCategoryIcon(isOpen)}
                                        </span>
                                        <span className="category-name">{category}</span>
                                        {isAdminMode && (
                                            <span className="task-count">({tasks.length})</span>
                                        )}
                                    </div>
                                    <span className="category-goto">{getGoToIcon()}</span>
                                </div>

                                {/* Liste des t√¢ches (si ouvert) */}
                                {isOpen && (
                                    <div className="tasks-list">
                                        {tasks.map((task, index) => (
                                            <div key={`task-container-${task.tabName}-${task.id}`}>
                                                {/* √âl√©ment de t√¢che - Plus facile √† attraper */}
                                                <div
                                                    className="task-item clickable draggable enhanced-drag"
                                                    draggable="true"
                                                    onDragStart={(e) => handleDragStart(e, task, category, index)}
                                                    onDragEnd={handleDragEnd}
                                                    onClick={() => !draggedItem && handleTaskClick(task)}
                                                    title="üñ±Ô∏è Glissez cette t√¢che vers une autre cat√©gorie"
                                                >
                                                    <span className="drag-handle enhanced-handle" title="Zone de glissement - Cliquez et glissez">
                                                        ‚â°‚â°
                                                    </span>
                                                    <span className="task-bullet">‚Ä¢</span>
                                                    <span className="task-name">
                                                        {task.fields?.find(f => f.key === 'modelNumber')?.value} - {task.name}
                                                    </span>
                                                </div>
                                            </div>
                                        ))}

                                        {/* Message pour cat√©gorie vide */}
                                        {tasks.length === 0 && (
                                            <div className="empty-category-drop-zone">
                                                üìÇ Glissez une t√¢che ici pour la d√©placer dans "{category}"
                                            </div>
                                        )}
                                    </div>
                                )}
                            </div>
                        );
                    })}
                </div>
            </div>
        </div>
    );
};

export default DashboardMaison;
