import React, { useState, useEffect, useMemo, useCallback } from 'react';
import MenuHaut from './components/MenuHaut';
import MenuGaucheAdjointes from './components/MenuGaucheAdjointes';
import DetailTacheAdjointe from './components/DetailTacheAdjointe';
import PanneauAdmin from './components/PanneauAdmin';
import DashboardMaison from './components/DashboardMaison';
import Notification from './components/Notification';
import ConfirmationModale from './components/ConfirmationModale';
import { loadAppDataFromLocalStorage, saveAppDataToLocalStorage } from './utils/localStorage';
import { initialAppData } from './data/initialData';
import { testAppData } from './data/testData'; // DonnÃ©es de test pour visualiser le layout 4 colonnes

// Composant principal de l'application
const App = () => {
    const LOCAL_STORAGE_KEY = 'covalenAppData';

    // --- Ã‰tats de l'application ---
    const [appData, setAppData] = useState(() => loadAppDataFromLocalStorage(LOCAL_STORAGE_KEY, initialAppData));
    const [activeTab, setActiveTab] = useState('Maison');
    const [activeTask, setActiveTask] = useState(null);
    const [isAdminMode, setIsAdminMode] = useState(false);
    const [notification, setNotification] = useState(null);
    const [categoryStates, setCategoryStates] = useState({});
    const [modalState, setModalState] = useState({ isOpen: false, message: '', onConfirm: () => { } });

    // --- Effets ---
    // Sauvegarde les donnÃ©es dans localStorage Ã  chaque modification
    // useEffect(() => {
    //     saveAppDataToLocalStorage(LOCAL_STORAGE_KEY, appData);
    // }, [appData]);

    // GÃ¨re la sÃ©lection de la tÃ¢che active lors du changement d'onglet
    useEffect(() => {
        if (activeTab !== 'Maison' && activeTab !== 'Admin') {
            const tasks = appData[activeTab] || [];
            if (tasks.length > 0) {
                // Si la tÃ¢che active n'est plus dans la liste, on sÃ©lectionne la premiÃ¨re
                if (!activeTask || !tasks.some(t => t.id === activeTask.id)) {
                    setActiveTask(tasks[0]);
                }
            } else {
                setActiveTask(null);
            }
        } else {
            setActiveTask(null);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [activeTab, appData]);

    // --- Fonctions utilitaires ---
    const showNotification = useCallback((message, type = 'info') => {
        setNotification({ message, type });
        setTimeout(() => setNotification(null), 3000);
    }, []);

    // --- Gestion des catÃ©gories (menu de gauche) ---
    const handleToggleCategory = useCallback((category) => {
        setCategoryStates(prev => ({
            ...prev,
            [category]: { ...prev[category], isOpen: !prev[category]?.isOpen }
        }));
    }, []);

    const categorizedTasks = useMemo(() => {
        const tasks = appData[activeTab] || [];
        if (!Array.isArray(tasks)) return {};

        return tasks.reduce((acc, task) => {
            const category = task.category || 'Autres';
            if (!acc[category]) {
                acc[category] = { tasks: [], isOpen: categoryStates[category]?.isOpen ?? true };
            }
            acc[category].tasks.push(task);
            return acc;
        }, {});
    }, [appData, activeTab, categoryStates]);


    // --- Gestion des onglets ---
    const handleTabChange = (tabName) => {
        setActiveTab(tabName);
        if (tabName !== 'Admin') {
            setIsAdminMode(false); // Quitter le mode admin en changeant d'onglet
        }
    };

    const handleAdminPanel = () => {
        setActiveTab('Admin');
        setIsAdminMode(true);
    };

    const handleExitAdmin = useCallback(() => {
        setActiveTab('Maison');
        setIsAdminMode(false);
    }, []);

    const handleSaveData = useCallback(() => {
        try {
            saveAppDataToLocalStorage(LOCAL_STORAGE_KEY, appData);
            showNotification('DonnÃ©es sauvegardÃ©es avec succÃ¨s !', 'success');
        } catch (error) {
            showNotification('Erreur lors de la sauvegarde.', 'error');
        }
    }, [appData, showNotification]);

    const handleAddTab = (newTabName) => {
        if (!newTabName.trim()) {
            showNotification('Le nom de l\'onglet ne peut pas Ãªtre vide.', 'error');
            return;
        }
        setAppData(prev => {
            if (prev[newTabName]) {
                showNotification(`L'onglet '${newTabName}' existe dÃ©jÃ .`, 'error');
                return prev;
            }
            showNotification(`Onglet '${newTabName}' ajoutÃ©.`, 'success');
            return { ...prev, [newTabName]: [] };
        });
    };

    const handleRenameTab = (oldTabName, newTabName) => {
        if (!newTabName.trim()) {
            showNotification('Le nouveau nom de l\'onglet ne peut pas Ãªtre vide.', 'error');
            return;
        }
        setAppData(prev => {
            if (prev[newTabName] && newTabName !== oldTabName) {
                showNotification(`Le nom '${newTabName}' est dÃ©jÃ  pris.`, 'error');
                return prev;
            }
            const { [oldTabName]: tabContent, ...rest } = prev;
            const newAppData = { ...rest, [newTabName]: tabContent };

            if (activeTab === oldTabName) {
                setActiveTab(newTabName);
            }
            showNotification(`Onglet '${oldTabName}' renommÃ© en '${newTabName}'.`, 'success');
            return newAppData;
        });
    };

    const confirmDeleteTab = (tabNameToDelete) => {
        setAppData(prev => {
            const newData = { ...prev };
            delete newData[tabNameToDelete];
            return newData;
        });
        if (activeTab === tabNameToDelete) {
            setActiveTab('Maison');
        }
        showNotification(`Onglet '${tabNameToDelete}' supprimÃ©.`, 'success');
        setModalState({ isOpen: false });
    };

    const handleDeleteTab = (tabNameToDelete) => {
        setModalState({
            isOpen: true,
            message: `ÃŠtes-vous sÃ»r de vouloir supprimer l'onglet "${tabNameToDelete}" et toutes ses tÃ¢ches ?`,
            onConfirm: () => confirmDeleteTab(tabNameToDelete)
        });
    };

    // --- Gestion des tÃ¢ches ---
    const handleUpdateTask = (updatedTask) => {
        setAppData(prev => ({
            ...prev,
            [activeTab]: (prev[activeTab] || []).map(task =>
                task.id === updatedTask.id ? updatedTask : task
            )
        }));
    };

    const handleAddTask = (targetTab, newTask) => {
        setAppData(prev => ({
            ...prev,
            [targetTab]: [...(prev[targetTab] || []), newTask]
        }));
        setActiveTask(newTask);
        setActiveTab(targetTab);
        showNotification('Nouvelle tÃ¢che ajoutÃ©e !', 'success');
    };

    const confirmDeleteTask = (taskId) => {
        let nextTask = null;
        setAppData(prev => {
            const tasks = prev[activeTab] || [];
            const taskIndex = tasks.findIndex(t => t.id === taskId);
            const updatedTasks = tasks.filter(t => t.id !== taskId);

            if (updatedTasks.length > 0) {
                // SÃ©lectionne la tÃ¢che suivante, ou la prÃ©cÃ©dente si c'Ã©tait la derniÃ¨re
                nextTask = updatedTasks[taskIndex] || updatedTasks[taskIndex - 1] || updatedTasks[0];
            }

            return { ...prev, [activeTab]: updatedTasks };
        });
        setActiveTask(nextTask);
        showNotification('TÃ¢che supprimÃ©e.', 'success');
        setModalState({ isOpen: false });
    };

    const handleDeleteTask = (taskId) => {
        const taskToDelete = (appData[activeTab] || []).find(t => t.id === taskId);
        if (taskToDelete) {
            setModalState({
                isOpen: true,
                message: `ÃŠtes-vous sÃ»r de vouloir supprimer la tÃ¢che "${taskToDelete.name}" ?`,
                onConfirm: () => confirmDeleteTask(taskId)
            });
        }
    };

    // --- Fonctions Drag & Drop ---
    const handleMoveTask = useCallback((task, targetCategory) => {
        console.log('ðŸŽ¯ handleMoveTask called:', { task, targetCategory });
        // Trouver l'onglet qui contient la tÃ¢che
        const sourceTab = task.tabName;

        setAppData(prev => {
            const updatedData = { ...prev };
            const sourceTasks = [...(updatedData[sourceTab] || [])];

            // Trouver et mettre Ã  jour la tÃ¢che avec la nouvelle catÃ©gorie
            const taskIndex = sourceTasks.findIndex(t => t.id === task.id);
            if (taskIndex !== -1) {
                console.log('âœ… Task found and updated');
                sourceTasks[taskIndex] = {
                    ...sourceTasks[taskIndex],
                    category: targetCategory
                };
                updatedData[sourceTab] = sourceTasks;

                // Mettre Ã  jour la tÃ¢che active si c'est celle qui a Ã©tÃ© dÃ©placÃ©e
                if (activeTask && activeTask.id === task.id) {
                    setActiveTask(sourceTasks[taskIndex]);
                }
            } else {
                console.log('âŒ Task not found');
            }

            return updatedData;
        });

        showNotification(`TÃ¢che "${task.name}" dÃ©placÃ©e vers la catÃ©gorie "${targetCategory}"`, 'success');
    }, [activeTask, showNotification]);

    const handleReorderCategories = useCallback((sourceCategory, targetCategory) => {
        // Cette fonction pourrait Ãªtre utilisÃ©e pour rÃ©organiser l'ordre des catÃ©gories
        // Pour l'instant, on peut l'implÃ©menter plus tard si nÃ©cessaire
        showNotification(`FonctionnalitÃ© de rÃ©organisation des catÃ©gories Ã  implÃ©menter`, 'info');
    }, [showNotification]);

    const handleReorderTasksInCategory = useCallback((category, sourceIndex, targetIndex) => {
        // Fonction pour rÃ©organiser l'ordre des tÃ¢ches dans une catÃ©gorie
        const reorderTasksInTab = (tasks, targetCategory, sIndex, tIndex) => {
            const categoryTasks = tasks.filter(t => (t.category || 'Autres') === targetCategory);

            if (categoryTasks.length <= 1 || sIndex === tIndex) {
                return tasks; // Pas de changement nÃ©cessaire
            }

            // RÃ©organiser les tÃ¢ches de cette catÃ©gorie
            const reorderedTasks = [...categoryTasks];
            const [removed] = reorderedTasks.splice(sIndex, 1);
            reorderedTasks.splice(tIndex, 0, removed);

            // Remettre les tÃ¢ches rÃ©organisÃ©es dans l'onglet
            const otherTasks = tasks.filter(t => (t.category || 'Autres') !== targetCategory);
            return [...otherTasks, ...reorderedTasks];
        };

        setAppData(prev => {
            const updatedData = { ...prev };

            // Appliquer la rÃ©organisation Ã  tous les onglets concernÃ©s
            Object.keys(updatedData).forEach(tabName => {
                if (tabName !== 'Maison' && tabName !== 'Admin') {
                    updatedData[tabName] = reorderTasksInTab(
                        updatedData[tabName] || [],
                        category,
                        sourceIndex,
                        targetIndex
                    );
                }
            });

            return updatedData;
        });

        showNotification(`TÃ¢ches rÃ©organisÃ©es dans la catÃ©gorie "${category}"`, 'success');
    }, [showNotification]);

    // --- Raccourcis clavier ---
    useEffect(() => {
        const handleKeyPress = (e) => {
            // Ctrl+S pour sauvegarder
            if (e.ctrlKey && e.key === 's') {
                e.preventDefault();
                if (isAdminMode) {
                    handleSaveData();
                }
            }

            // Ctrl+/ pour focus sur la recherche (dashboard)
            if (e.ctrlKey && e.key === '/') {
                e.preventDefault();
                if (activeTab === 'Maison') {
                    const searchInput = document.querySelector('.search-input');
                    if (searchInput) {
                        searchInput.focus();
                        showNotification('Focus sur la recherche', 'info');
                    }
                }
            }

            // Ã‰chap pour sortir du mode admin
            if (e.key === 'Escape' && isAdminMode) {
                handleExitAdmin();
                showNotification('Mode admin quittÃ©', 'info');
            }
        };

        document.addEventListener('keydown', handleKeyPress);
        return () => document.removeEventListener('keydown', handleKeyPress);
    }, [isAdminMode, activeTab, handleSaveData, handleExitAdmin, showNotification]);

    return (
        <div className="min-h-screen bg-gray-100 font-sans antialiased flex flex-col">
            <MenuHaut
                tabs={Object.keys(appData)}
                activeTab={activeTab}
                onTabClick={handleTabChange}
                onAdminPanel={handleAdminPanel}
                onExitAdmin={handleExitAdmin}
                isAdminMode={isAdminMode}
                onLogout={() => showNotification('Fonction de dÃ©connexion Ã  implÃ©menter.', 'info')}
            />

            <main className="flex flex-1 p-6 max-w-screen-2xl mx-auto w-full overflow-hidden">
                {activeTab === 'Maison' && (
                    <DashboardMaison
                        appData={appData}
                        onTabChange={handleTabChange}
                        onTaskSelect={setActiveTask}
                        onAdminPanel={handleAdminPanel}
                        isAdminMode={isAdminMode}
                        onMoveTask={handleMoveTask}
                        onReorderCategories={handleReorderCategories}
                        onReorderTasksInCategory={handleReorderTasksInCategory}
                    />
                )}

                {activeTab === 'Admin' && (
                    <PanneauAdmin
                        appData={appData}
                        onAddTab={handleAddTab}
                        onAddTask={handleAddTask}
                        onRenameTab={handleRenameTab}
                        onDeleteTab={handleDeleteTab}
                        onSaveData={handleSaveData}
                        showNotification={showNotification}
                    />
                )}

                {activeTab !== 'Admin' && activeTab !== 'Maison' && (
                    <>
                        <MenuGaucheAdjointes
                            categories={categorizedTasks}
                            onSelectTask={setActiveTask}
                            selectedTask={activeTask}
                            onToggleCategory={handleToggleCategory}
                        />
                        <DetailTacheAdjointe
                            task={activeTask}
                            onUpdateTask={handleUpdateTask}
                            onAdminModeToggle={setIsAdminMode}
                            isAdminMode={isAdminMode}
                            onDeleteTask={handleDeleteTask}
                            showNotification={showNotification}
                        />
                    </>
                )}
            </main>

            <Notification
                message={notification?.message}
                type={notification?.type}
                onClose={() => setNotification(null)}
            />

            <ConfirmationModale
                isOpen={modalState.isOpen}
                message={modalState.message}
                onConfirm={modalState.onConfirm}
                onCancel={() => setModalState({ isOpen: false })}
            />
        </div>
    );
};

export default App;
