import React, { useState, useMemo } from 'react';

const DashboardMaison = ({ appData, onTabChange, onTaskSelect, onAdminPanel, isAdminMode, onMoveTask, onReorderCategories, onReorderTasksInCategory }) => {
    const [categoryStates, setCategoryStates] = useState({});
    const [searchTerm, setSearchTerm] = useState('');
    const [draggedItem, setDraggedItem] = useState(null);
    const [dragOverCategory, setDragOverCategory] = useState(null);    // Fonction pour regrouper toutes les tâches par catégorie
    const getAllTasksByCategory = () => {
        const allCategories = {};

        // Parcourir tous les onglets sauf 'Maison' et 'Admin'
        Object.keys(appData).forEach(tabName => {
            if (tabName !== 'Maison' && tabName !== 'Admin') {
                const tasks = appData[tabName] || [];
                tasks.forEach(task => {
                    const category = task.category || 'Autres';
                    if (!allCategories[category]) {
                        allCategories[category] = [];
                    }
                    allCategories[category].push({
                        ...task,
                        tabName
                    });
                });
            }
        });

        return allCategories;
    };

    const allTasksByCategory = getAllTasksByCategory();

    // Filtrer les tâches selon le terme de recherche
    const filteredCategories = useMemo(() => {
        if (!searchTerm) return allTasksByCategory;

        const filtered = {};
        const searchLower = searchTerm.toLowerCase();

        const taskMatchesSearch = (task) => {
            const modelNumber = task.fields?.find(f => f.key === 'modelNumber')?.value || '';
            return (
                task.name.toLowerCase().includes(searchLower) ||
                modelNumber.toLowerCase().includes(searchLower)
            );
        };

        Object.keys(allTasksByCategory).forEach(category => {
            const matchingTasks = allTasksByCategory[category].filter(taskMatchesSearch);
            const categoryMatches = category.toLowerCase().includes(searchLower);

            if (matchingTasks.length > 0 || categoryMatches) {
                filtered[category] = matchingTasks;
            }
        });

        return filtered;
    }, [allTasksByCategory, searchTerm]);

    const categorizedTasks = filteredCategories;

    // --- Fonctions Drag & Drop ---
    const handleDragStart = (e, item, type, index = null) => {
        setDraggedItem({ item, type, index });
        e.dataTransfer.effectAllowed = 'move';
        e.target.style.opacity = '0.5';

        // Ajouter des données pour le transfert
        e.dataTransfer.setData('text/plain', JSON.stringify({ item, type, index }));
    };

    const handleDragEnd = (e) => {
        e.target.style.opacity = '1';
        setDraggedItem(null);
        setDragOverCategory(null);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
        e.dataTransfer.dropEffect = 'move';
    };

    const handleDragEnter = (e, category) => {
        e.preventDefault();
        setDragOverCategory(category);
    };

    const handleDragLeave = (e) => {
        if (!e.currentTarget.contains(e.relatedTarget)) {
            setDragOverCategory(null);
        }
    };

    const handleDrop = (e, targetCategory, targetIndex = null) => {
        e.preventDefault();
        setDragOverCategory(null);

        if (!draggedItem) return;

        const { item, type, index: sourceIndex } = draggedItem;

        if (type === 'task') {
            const sourceCategory = item.category || 'Autres';

            if (targetCategory !== sourceCategory) {
                // Déplacer la tâche vers une nouvelle catégorie
                if (onMoveTask) {
                    onMoveTask(item, targetCategory);
                }
            } else if (targetIndex !== null && sourceIndex !== null && sourceIndex !== targetIndex) {
                // Réorganiser les tâches dans la même catégorie
                if (onReorderTasksInCategory) {
                    onReorderTasksInCategory(sourceCategory, sourceIndex, targetIndex);
                }
            }
        } else if (type === 'category' && onReorderCategories) {
            // Réorganiser les catégories
            onReorderCategories(item, targetCategory);
        }

        setDraggedItem(null);
    };

    const handleTaskDrop = (e, targetCategory, targetIndex) => {
        e.stopPropagation();
        handleDrop(e, targetCategory, targetIndex);
    };

    const handleToggleCategory = (category, event) => {
        event.stopPropagation();
        setCategoryStates(prev => ({
            ...prev,
            [category]: !prev[category]
        }));
    };

    const handleTaskClick = (task) => {
        // Naviguer vers l'onglet et sélectionner la tâche
        onTabChange(task.tabName);
        onTaskSelect(task);
    };

    const getCategoryIcon = (isOpen) => {
        return isOpen ? '▼' : '▶';
    };

    const getGoToIcon = () => {
        return '→';
    };

    return (
        <div className="dashboard-container">
            {/* En-tête principal */}
            <div className="dashboard-header">
                <h1 className="dashboard-title">Covalen</h1>
            </div>

            {/* Message de bienvenue */}
            <p className="welcome-message">
                Bienvenue sur votre tableau de bord centralisé.
            </p>

            {/* Section Admin - seulement visible en mode admin */}
            {isAdminMode && (
                <div className="admin-dashboard-section">
                    <div className="admin-banner">
                        <p className="admin-banner-text">Mode Admin Actif sur le Dashboard.</p>
                        <button className="admin-manage-btn" onClick={onAdminPanel}>
                            Gérer les Onglets
                        </button>
                    </div>
                </div>
            )}            {/* Section Vue d'overview */}
            <div className="overview-section">
                <h2 className="overview-title">Vue d'overview des Tâches</h2>

                {/* Barre de recherche */}
                <div className="search-container">
                    <div className="search-bar">
                        <span className="search-icon">🔍</span>
                        <input
                            type="text"
                            placeholder="Rechercher une tâche, catégorie ou numéro..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="search-input"
                        />
                        {searchTerm && (
                            <button
                                className="search-clear"
                                onClick={() => setSearchTerm('')}
                                title="Effacer la recherche"
                            >
                                ✕
                            </button>
                        )}
                    </div>
                    <div className="search-hints">
                        <span className="search-hint">💡 Raccourci: Ctrl+/ pour rechercher</span>
                    </div>
                    {searchTerm && (
                        <div className="search-results-info">
                            {Object.keys(categorizedTasks).length === 0 
                                ? `Aucun résultat pour "${searchTerm}"` 
                                : `${Object.values(categorizedTasks).flat().length} résultat(s) trouvé(s)`
                            }
                        </div>
                    )}
                </div>

                {/* Conteneur des catégories */}
                <div className="categories-container">
                    <div className="tasks-header">
                        <span className="tasks-label">Tâches</span>
                    </div>

                    {/* Liste des catégories */}
                    {Object.keys(categorizedTasks).map(category => {
                        const isOpen = categoryStates[category] ?? true;
                        const tasks = categorizedTasks[category];

                        return (
                            <div 
                                key={category} 
                                className={`category-section ${dragOverCategory === category ? 'drag-over' : ''}`}
                                onDragOver={handleDragOver}
                                onDragEnter={(e) => handleDragEnter(e, category)}
                                onDragLeave={handleDragLeave}
                                onDrop={(e) => handleDrop(e, category)}
                            >
                                {/* En-tête de catégorie */}
                                <div
                                    className={`category-header ${isAdminMode ? 'admin-mode' : ''}`}
                                    onClick={(e) => handleToggleCategory(category, e)}
                                >
                                    <div className="category-left">
                                        {isAdminMode && <span className="drag-handle">⋮⋮</span>}
                                        <span className="category-toggle">
                                            {getCategoryIcon(isOpen)}
                                        </span>
                                        <span className="category-name">{category}</span>
                                        {isAdminMode && (
                                            <span className="task-count">({tasks.length})</span>
                                        )}
                                    </div>
                                    <span className="category-goto">{getGoToIcon()}</span>
                                </div>

                                {/* Liste des tâches (si ouvert) */}
                                {isOpen && (
                                    <div className="tasks-list">
                                        {tasks.map((task, index) => (
                                            <div key={`task-container-${task.tabName}-${task.id}`}>
                                                {/* Zone de drop avant la tâche (en mode admin uniquement) */}
                                                {isAdminMode && (
                                                    <div 
                                                        className="task-drop-zone"
                                                        onDragOver={handleDragOver}
                                                        onDrop={(e) => handleTaskDrop(e, category, index)}
                                                        style={{
                                                            height: '2px',
                                                            backgroundColor: dragOverCategory === category && draggedItem?.type === 'task' ? '#3B82F6' : 'transparent',
                                                            transition: 'background-color 0.2s'
                                                        }}
                                                    />
                                                )}

                                                {/* Élément de tâche */}
                                                <div
                                                    className={`task-item clickable ${isAdminMode ? 'draggable' : ''}`}
                                                    draggable={isAdminMode}
                                                    onDragStart={(e) => handleDragStart(e, { ...task, category }, 'task', index)}
                                                    onDragEnd={handleDragEnd}
                                                    onClick={() => !draggedItem && handleTaskClick(task)}
                                                    title={isAdminMode ? "Glissez pour déplacer vers une autre catégorie ou réorganiser" : ""}
                                                >
                                                    {isAdminMode && <span className="drag-handle">⋮⋮</span>}
                                                    <span className="task-bullet">•</span>
                                                    <span className="task-name">
                                                        {task.fields?.find(f => f.key === 'modelNumber')?.value} - {task.name}
                                                    </span>
                                                </div>
                                            </div>
                                        ))}

                                        {/* Zone de drop finale (en mode admin uniquement) */}
                                        {isAdminMode && (
                                            <div 
                                                className="task-drop-zone"
                                                onDragOver={handleDragOver}
                                                onDrop={(e) => handleTaskDrop(e, category, tasks.length)}
                                                style={{
                                                    height: '10px',
                                                    backgroundColor: dragOverCategory === category && draggedItem?.type === 'task' ? '#3B82F6' : 'transparent',
                                                    transition: 'background-color 0.2s',
                                                    borderRadius: '2px'
                                                }}
                                            />
                                        )}

                                        {/* Message pour catégorie vide */}
                                        {isAdminMode && tasks.length === 0 && (
                                            <div className="empty-category-drop-zone">
                                                Glissez une tâche ici pour la déplacer dans "{category}"
                                            </div>
                                        )}
                                    </div>
                                )}
                            </div>
                        );
                    })}
                </div>
            </div>
        </div>
    );
};

export default DashboardMaison;
