import React, { useState, useMemo } from 'react';
import { useDrag, useDrop } from 'react-dnd';

// Types de drag & drop
const ItemTypes = {
    TASK: 'task'
};

// Composant Task draggable
const DraggableTask = ({ task, category, index, onTaskClick, onMoveTask, onReorderTask }) => {
    const [{ isDragging }, dragRef] = useDrag(() => ({
        type: ItemTypes.TASK,
        item: {
            type: ItemTypes.TASK,
            task,
            category,
            index,
            sourceCategory: category,
            sourceIndex: index
        },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    }), [task, category, index]);

    // Log du début/fin de drag
    React.useEffect(() => {
        if (isDragging) {
            console.log('🚀 Drag started:', { task: task.name, category, index });
        }
    }, [isDragging, task.name, category, index]);

    return (
        <div
            ref={dragRef}
            className={`task-item clickable draggable enhanced-drag compact ${isDragging ? 'dragging' : ''}`}
            onClick={() => !isDragging && onTaskClick(task)}
            title="🖱️ Glissez pour réorganiser ou changer de catégorie"
            style={{ opacity: isDragging ? 0.6 : 1 }}
        >
            <div className="task-content-compact">
                <span className="drag-handle enhanced-handle compact-handle" title="Réorganiser">
                    ⋮⋮
                </span>
                <div className="task-info">
                    <span className="task-number">
                        {task.fields?.find(f => f.key === 'modelNumber')?.value}
                    </span>
                    <span className="task-name-compact">
                        {task.name}
                    </span>
                </div>
            </div>
        </div>
    );
};

// Zone de drop entre les tâches
const DropZone = ({ category, index, onMoveTask, onReorderTask, isLast = false }) => {
    const [{ isOver, canDrop }, dropRef] = useDrop(() => ({
        accept: ItemTypes.TASK,
        drop: (item, monitor) => {
            // Empêcher la propagation vers la catégorie
            monitor.getDropResult = () => ({ dropEffect: 'move' });

            console.log('🎯 Drop on:', { category, index, item });

            const { sourceCategory, sourceIndex } = item;

            // Seulement autoriser les déplacements dans la même catégorie
            if (sourceCategory === category) {
                // Réorganisation dans la même catégorie
                let targetIndex = index;

                // Ajustement d'index pour éviter les comportements aléatoires
                if (sourceIndex < index) {
                    targetIndex = index - 1;
                }

                if (sourceIndex !== targetIndex && targetIndex >= 0) {
                    console.log('🔄 Reordering task in same category:', sourceIndex, '→', targetIndex);
                    onReorderTask(category, sourceIndex, targetIndex);
                }
            } else {
                console.log('❌ Cross-category drop BLOCKED in task zones:', sourceCategory, '→', category);
            }
        },
        canDrop: (item, monitor) => {
            // Seulement autoriser le drop si c'est la même catégorie
            return item.sourceCategory === category;
        },
        collect: (monitor) => ({
            isOver: monitor.isOver(),
            canDrop: monitor.canDrop(),
        }),
    }), [category, index, onMoveTask, onReorderTask]);

    const showDropIndicator = isOver && canDrop;
    const isDragOverDifferentCategory = isOver && !canDrop;

    return (
        <div
            ref={dropRef}
            className={`task-drop-zone-vertical ${showDropIndicator ? 'drag-over' : ''} ${isDragOverDifferentCategory ? 'blocked-drop' : ''} ${isLast ? 'final' : ''}`}
            title={isLast ? "Déposez ici pour ajouter à la fin" : "Déposez ici pour insérer avant cette tâche"}
        >
            {showDropIndicator && !isLast && (
                <div className="drop-indicator">
                    ▼ Insérer ici ▼
                </div>
            )}
            {showDropIndicator && isLast && (
                <div className="drop-indicator final">
                    ⬇️ Ajouter à la fin
                </div>
            )}
            {isDragOverDifferentCategory && (
                <div className="drop-indicator blocked">
                    ❌ Drop inter-catégories bloqué
                </div>
            )}
        </div>
    );
};

// Zone de drop pour catégories vides
const EmptyDropZone = ({ category, onMoveTask }) => {
    const [{ isOver, canDrop }, dropRef] = useDrop(() => ({
        accept: ItemTypes.TASK,
        drop: (item, monitor) => {
            console.log('🎯 Drop on empty category:', category);
            const { sourceCategory, task } = item;

            // STRICTEMENT EMPÊCHER le déplacement inter-catégories
            if (sourceCategory === category) {
                console.log('✅ Allowing drop in same category (empty zone)');
                // Même catégorie : on peut réorganiser (ajouter à la fin de la catégorie vide)
                onMoveTask(task, category);
            } else {
                console.log('❌ Cross-category drop BLOCKED in empty zone:', sourceCategory, '→', category);
                // Ne rien faire : drop bloqué
            }
        },
        canDrop: (item, monitor) => {
            // Seulement autoriser le drop si c'est la même catégorie
            return item.sourceCategory === category;
        },
        collect: (monitor) => ({
            isOver: monitor.isOver(),
            canDrop: monitor.canDrop(),
        }),
    }), [category, onMoveTask]);

    const showDropIndicator = isOver && canDrop;
    const isDragOverDifferentCategory = isOver && !canDrop;

    return (
        <div
            ref={dropRef}
            className={`empty-category-drop-zone grid-full-width ${showDropIndicator ? 'drag-over' : ''} ${isDragOverDifferentCategory ? 'blocked-drop' : ''}`}
        >
            📂 Aucune tâche dans "{category}"
            {showDropIndicator && (
                <div className="empty-drop-indicator">
                    ✨ Relâchez pour ajouter à cette catégorie
                </div>
            )}
            {isDragOverDifferentCategory && (
                <div className="empty-drop-indicator blocked">
                    ❌ Drop inter-catégories non autorisé
                </div>
            )}
        </div>
    );
};

const DashboardMaison = ({ appData, onTabChange, onTaskSelect, onAdminPanel, isAdminMode, onMoveTask, onReorderCategories, onReorderTasksInCategory }) => {
    const [categoryStates, setCategoryStates] = useState({});
    const [searchTerm, setSearchTerm] = useState('');

    const getAllTasksByCategory = () => {
        const allCategories = {};

        // Parcourir tous les onglets sauf 'Maison' et 'Admin'
        Object.keys(appData).forEach(tabName => {
            if (tabName !== 'Maison' && tabName !== 'Admin') {
                const tasks = appData[tabName] || [];
                tasks.forEach(task => {
                    const category = task.category || 'Autres';
                    if (!allCategories[category]) {
                        allCategories[category] = [];
                    }
                    allCategories[category].push({
                        ...task,
                        tabName
                    });
                });
            }
        });

        return allCategories;
    };

    const allTasksByCategory = getAllTasksByCategory();

    // Filtrer les tâches selon le terme de recherche
    const filteredCategories = useMemo(() => {
        if (!searchTerm) return allTasksByCategory;

        const filtered = {};
        const searchLower = searchTerm.toLowerCase();

        const taskMatchesSearch = (task) => {
            const modelNumber = task.fields?.find(f => f.key === 'modelNumber')?.value || '';
            return (
                task.name.toLowerCase().includes(searchLower) ||
                modelNumber.toLowerCase().includes(searchLower)
            );
        };

        Object.keys(allTasksByCategory).forEach(category => {
            const matchingTasks = allTasksByCategory[category].filter(taskMatchesSearch);
            const categoryMatches = category.toLowerCase().includes(searchLower);

            if (matchingTasks.length > 0 || categoryMatches) {
                filtered[category] = matchingTasks;
            }
        });

        return filtered;
    }, [allTasksByCategory, searchTerm]);

    const categorizedTasks = filteredCategories;

    const handleToggleCategory = (category, event) => {
        event.stopPropagation();
        setCategoryStates(prev => ({
            ...prev,
            [category]: !prev[category]
        }));
    };

    const handleTaskClick = (task) => {
        // Naviguer vers l'onglet et sélectionner la tâche
        onTabChange(task.tabName);
        onTaskSelect(task);
    };

    const getCategoryIcon = (isOpen) => {
        return isOpen ? '▼' : '▶';
    };

    const getGoToIcon = () => {
        return '→';
    };

    return (
        <div className="dashboard-container">
            {/* En-tête principal */}
            <div className="dashboard-header">
                <h1 className="dashboard-title">Covalen</h1>
            </div>

            {/* Message de bienvenue */}
            <p className="welcome-message">
                Bienvenue sur votre tableau de bord centralisé.
            </p>

            {/* Section Admin - seulement visible en mode admin */}
            {isAdminMode && (
                <div className="admin-dashboard-section">
                    <div className="admin-banner">
                        <p className="admin-banner-text">Mode Admin Actif sur le Dashboard.</p>
                        <button className="admin-manage-btn" onClick={onAdminPanel}>
                            Gérer les Onglets
                        </button>
                    </div>
                </div>
            )}

            {/* Section Vue d'overview */}
            <div className="overview-section">
                <h2 className="overview-title">Vue d'overview des Tâches</h2>

                {/* Barre de recherche */}
                <div className="search-container">
                    <div className="search-bar">
                        <span className="search-icon">🔍</span>
                        <input
                            type="text"
                            placeholder="Rechercher une tâche, catégorie ou numéro..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="search-input"
                        />
                        {searchTerm && (
                            <button
                                className="search-clear"
                                onClick={() => setSearchTerm('')}
                                title="Effacer la recherche"
                            >
                                ✕
                            </button>
                        )}
                    </div>
                    <div className="search-hints">
                        <span className="search-hint">💡 Raccourci: Ctrl+/ pour rechercher</span>
                    </div>
                    {searchTerm && (
                        <div className="search-results-info">
                            {Object.keys(categorizedTasks).length === 0
                                ? `Aucun résultat pour "${searchTerm}"`
                                : `${Object.values(categorizedTasks).flat().length} résultat(s) trouvé(s)`
                            }
                        </div>
                    )}
                </div>

                {/* Conteneur des catégories */}
                <div className="categories-container">
                    <div className="tasks-header">
                        <span className="tasks-label">Tâches</span>
                    </div>

                    {/* Liste des catégories */}
                    {Object.keys(categorizedTasks).map(category => {
                        const isOpen = categoryStates[category] ?? true;
                        const tasks = categorizedTasks[category];

                        return (
                            <div key={category} className="category-section">
                                {/* En-tête de catégorie */}
                                <div
                                    className={`category-header ${isAdminMode ? 'admin-mode' : ''}`}
                                    onClick={(e) => handleToggleCategory(category, e)}
                                >
                                    <div className="category-left">
                                        {isAdminMode && <span className="drag-handle">⋮⋮</span>}
                                        <span className="category-toggle">
                                            {getCategoryIcon(isOpen)}
                                        </span>
                                        <span className="category-name">{category}</span>
                                        {isAdminMode && (
                                            <span className="task-count">({tasks.length})</span>
                                        )}
                                    </div>
                                    <span className="category-goto">{getGoToIcon()}</span>
                                </div>

                                {/* Liste des tâches en grille 4 colonnes (si ouvert) */}
                                {isOpen && (
                                    <div className="tasks-grid">
                                        {tasks.length === 0 ? (
                                            <EmptyDropZone
                                                category={category}
                                                onMoveTask={onMoveTask}
                                            />
                                        ) : (
                                            <>
                                                {tasks.map((task, index) => (
                                                    <div key={`task-wrapper-${task.tabName}-${task.id}`} className="task-grid-item">
                                                        {/* Zone de drop avant la tâche */}
                                                        <DropZone
                                                            category={category}
                                                            index={index}
                                                            onMoveTask={onMoveTask}
                                                            onReorderTask={onReorderTasksInCategory}
                                                        />

                                                        {/* Tâche draggable */}
                                                        <DraggableTask
                                                            task={task}
                                                            category={category}
                                                            index={index}
                                                            onTaskClick={handleTaskClick}
                                                            onMoveTask={onMoveTask}
                                                            onReorderTask={onReorderTasksInCategory}
                                                        />
                                                    </div>
                                                ))}

                                                {/* Zone de drop finale */}
                                                <DropZone
                                                    category={category}
                                                    index={tasks.length}
                                                    onMoveTask={onMoveTask}
                                                    onReorderTask={onReorderTasksInCategory}
                                                    isLast={true}
                                                />
                                            </>
                                        )}
                                    </div>
                                )}
                            </div>
                        );
                    })}
                </div>
            </div>
        </div>
    );
};

export default DashboardMaison;
