import React, { useState } from 'react';

const PanneauAdmin = ({ appData, onAddTab, onAddTask, onRenameTab, onDeleteTab, showNotification }) => {
    const [newTabName, setNewTabName] = useState('');
    const [newTask, setNewTask] = useState({
        targetTab: 'Adjointes',
        name: '',
        category: '',
        modelNumber: '',
        attachmentName: '',
        noteTemplate: '',
        procedure: '',
        fullTemplateName: '',
        adminOnlyEdit: false
    });

    // Obtenir la liste des onglets (sans Maison et Admin)
    const availableTabs = Object.keys(appData).filter(tab => tab !== 'Maison' && tab !== 'Admin');

    const handleAddTab = () => {
        if (newTabName.trim()) {
            onAddTab(newTabName.trim());
            setNewTabName('');
        }
    };

    const handleAddTask = () => {
        if (newTask.name.trim() && newTask.targetTab) {
            const taskToAdd = {
                id: Date.now().toString(),
                name: newTask.name.trim(),
                category: newTask.category.trim() || 'Autres',
                fields: [
                    { key: 'modelNumber', label: 'Numéro de Modèle/Lettre', type: 'text', value: newTask.modelNumber.trim(), adminOnlyEdit: true },
                    { key: 'fullTemplateName', label: 'Modèle de lettre', type: 'text', value: newTask.fullTemplateName.trim(), adminOnlyEdit: true, copyable: true },
                    { key: 'attachmentName', label: 'Nom de Pièce Jointe', type: 'text', value: newTask.attachmentName.trim(), adminOnlyEdit: false, copyable: true },
                    { key: 'noteTemplate', label: 'Note EPIC', type: 'textarea', value: newTask.noteTemplate.trim(), adminOnlyEdit: false, copyable: true, specialButton: 'addDate' },
                    { key: 'procedure', label: 'Procédure à Suivre', type: 'textarea', value: newTask.procedure.trim(), adminOnlyEdit: true, hasCheckboxes: true },
                ]
            };

            onAddTask(newTask.targetTab, taskToAdd);

            // Reset du formulaire
            setNewTask({
                targetTab: 'Adjointes',
                name: '',
                category: '',
                modelNumber: '',
                attachmentName: '',
                noteTemplate: '',
                procedure: '',
                fullTemplateName: '',
                adminOnlyEdit: false
            });
        }
    };

    return (
        <div className="admin-panel">
            <div className="admin-header">
                <h1 className="admin-title">Panneau d'Administration</h1>
                <button className="quit-admin-btn">Quitter Admin</button>
            </div>

            {/* Section Gérer les Onglets */}
            <div className="admin-section">
                <h2 className="section-title">Gérer les Onglets Principaux</h2>
                <div className="tabs-management">
                    {availableTabs.map(tabName => (
                        <div key={tabName} className="tab-row">
                            <span className="tab-name">{tabName}</span>
                            <div className="tab-actions">
                                <button
                                    className="btn-rename"
                                    onClick={() => {
                                        const newName = prompt(`Nouveau nom pour "${tabName}":`, tabName);
                                        if (newName && newName.trim() !== tabName) {
                                            onRenameTab(tabName, newName.trim());
                                        }
                                    }}
                                >
                                    Renommer
                                </button>
                                <button
                                    className="btn-delete"
                                    onClick={() => onDeleteTab(tabName)}
                                >
                                    Supprimer
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* Section Ajouter Nouvel Onglet */}
            <div className="admin-section">
                <h3 className="subsection-title">Ajouter un Nouvel Onglet</h3>
                <div className="add-tab-form">
                    <input
                        type="text"
                        placeholder="Nom du nouvel onglet (ex: Finances)"
                        value={newTabName}
                        onChange={(e) => setNewTabName(e.target.value)}
                        className="tab-input"
                    />
                    <button onClick={handleAddTab} className="btn-add-tab">
                        Ajouter Onglet
                    </button>
                </div>
            </div>

            {/* Section Ajouter Nouvelle Tâche */}
            <div className="admin-section">
                <h3 className="subsection-title">Ajouter une Nouvelle Tâche</h3>
                <div className="add-task-form">
                    <div className="form-row">
                        <label>Ajouter la tâche à l'onglet:</label>
                        <select
                            value={newTask.targetTab}
                            onChange={(e) => setNewTask({ ...newTask, targetTab: e.target.value })}
                            className="task-select"
                        >
                            {availableTabs.map(tab => (
                                <option key={tab} value={tab}>{tab}</option>
                            ))}
                        </select>
                    </div>

                    <div className="form-row">
                        <label>Nom de la Tâche:</label>
                        <input
                            type="text"
                            value={newTask.name}
                            onChange={(e) => setNewTask({ ...newTask, name: e.target.value })}
                            className="task-input"
                        />
                    </div>

                    <div className="form-row">
                        <label>Catégorie de la Tâche (pour Dashboard):</label>
                        <input
                            type="text"
                            placeholder="ex: Annulations, Provisions, Rappels"
                            value={newTask.category}
                            onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
                            className="task-input"
                        />
                    </div>

                    <div className="form-row">
                        <label>Fonction de Modèle/Lettres:</label>
                        <input
                            type="text"
                            value={newTask.fullTemplateName}
                            onChange={(e) => setNewTask({ ...newTask, fullTemplateName: e.target.value })}
                            className="task-input"
                        />
                    </div>

                    <div className="form-row">
                        <label>Numéro de Modèle/Lettres:</label>
                        <input
                            type="text"
                            value={newTask.modelNumber}
                            onChange={(e) => setNewTask({ ...newTask, modelNumber: e.target.value })}
                            className="task-input"
                        />
                    </div>

                    <div className="form-row">
                        <label>Nom de Pièce Jointe:</label>
                        <input
                            type="text"
                            value={newTask.attachmentName}
                            onChange={(e) => setNewTask({ ...newTask, attachmentName: e.target.value })}
                            className="task-input"
                        />
                    </div>

                    <div className="form-row">
                        <label>Note EPIC:</label>
                        <textarea
                            value={newTask.noteTemplate}
                            onChange={(e) => setNewTask({ ...newTask, noteTemplate: e.target.value })}
                            className="task-textarea"
                            rows="3"
                        />
                    </div>

                    <div className="form-row">
                        <label>Procédure à Suivre:</label>
                        <textarea
                            value={newTask.procedure}
                            onChange={(e) => setNewTask({ ...newTask, procedure: e.target.value })}
                            className="task-textarea"
                            rows="5"
                        />
                    </div>

                    <div className="form-row">
                        <label>
                            <input
                                type="checkbox"
                                checked={newTask.adminOnlyEdit}
                                onChange={(e) => setNewTask({ ...newTask, adminOnlyEdit: e.target.checked })}
                            />
                            Admin peut modifier pour vos catégories
                        </label>
                    </div>

                    <button onClick={handleAddTask} className="btn-add-task">
                        Ajouter Tâche
                    </button>
                </div>
            </div>
        </div>
    );
};

export default PanneauAdmin;
